import React from "react";
import PaginationComponent from "./pagination.component";
import FormatDate from "../utils/formatDate";

class ResultTableComponent extends React.Component {
  constructor(props) {
    super(props);
    // this.doSearch = this.doSearch.bind(this);
    this.state = { word: "", tweets: [], repeated: [] };
  }

  render() {
    return (
      <div className="ResultTable">
        <PaginationComponent />

        {this.state.tweets.length} result(s) of limit of 15.

        {this.state.repeated.map((element, i) => {
          return (
            <div key={i} >{element}</div>
          )
        })}

        <h1>{this.state.word}</h1>
        <table id="dtBasicExample" className="table table-striped table-bordered table-sm" cellSpacing="0" width="100%">
          <thead><TH /></thead>
          <tbody>
            {this.state.tweets.map((tweet, i) => {
              return (
                <tr key={i} >
                  <td><img alt="photo_profile" src={tweet.profile_image} /></td>
                  <td>{tweet.user}</td>
                  <td>{tweet.location ? tweet.location : " - "}</td>
                  <td>{new FormatDate(new Date(tweet.created_at)).getMonthNameShort()}</td>
                  <td>{tweet.text}</td>
                </tr>
              )
            })}
          </tbody>
          <tfoot><TH /></tfoot>
        </table>
        <PaginationComponent />
      </div >
    );
  }

  loadData(tweets) {
    this.setState({ tweets: tweets });
    var createdDates = [];
    this.state.tweets.forEach(element => {
      createdDates.push(new FormatDate(new Date(element.created_at)).getMonthNameShort())
    });
    this.count(createdDates);
    this.forceUpdate()
  }

  count(array_elements) {

    this.setState({ repeated: [] });
    array_elements.sort();
    var current = null;
    var cnt = 0;
    for (var i = 0; i < array_elements.length; i++) {
      if (array_elements[i] !== current) {
        if (cnt > 0) {
          this.state.repeated.push(((cnt/array_elements.length)*100) + '% Tweets found in ' + current);
        }
        current = array_elements[i];
        cnt = 1;
      } else {
        cnt++;
      }
    }
    if (cnt > 0) {
      this.state.repeated.push( ((cnt/array_elements.length)*100) + '% Tweets found in ' + current);
    }

  }
}

const TH = () => {
  return (
    <tr>
      <th className="th-sm">profile</th>
      <th className="th-sm">user</th>
      <th className="th-sm">location</th>
      <th className="th-sm">created</th>
      <th className="th-sm">tweet</th>
    </tr>
  );

}




export default ResultTableComponent;
