import React from "react";
import axios from "axios";
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";
import HeaderComponent from "../src/components/header.component";
import InputSearchComponent from "../src/components/input.search.component";
import ResultTableComponent from "../src/components/result.table.component";

export default class App extends React.Component {
  state = {
    tweets: ""
  }

  constructor(props) {
    super(props);
    this.handleChange = this.handleChange.bind(this);
    this.state = { temperature: "" };
  }

  handleChange(e) {
    this.setState({ temperature: e.target.value });
  }

  render() {
    // const { tweets } = this.state;
    const temperature = "zumbi";
    this.getTweets(temperature);
    return (
      <Router>
        <div>
          <Link to="/" label="Home">Home</Link>
          <Link to="/statistics" label="About">Statistics</Link>
          <hr />

          <div className="App container">
            {window.tweets}
            <HeaderComponent
              title="Mockup for TwitterAPI"
              description="ReactJS + ExpressJS + NodeJS + TwitterAPI"
            />
            <InputSearchComponent value={temperature} onChange={this.handleChange}/>
          </div>
          <div className="resultArea">
            <div className="container">
              <ResultTableComponent celsius={parseFloat(temperature)} />
            </div>
          </div>

          <Switch>
            <Route exact path="/">
              {/* <Home /> */}
            </Route>
            <Route path="/statistics">
              {/* <About /> */}
            </Route>
          </Switch>
        </div>
      </Router>
    )
  }

  async getTweets(temperature) {
    this.resultTest.test("AAA");
    try {
      const result = await axios.get("http://localhost:4000/" + temperature);
      // window.tweets =   JSON.stringify(result.data) ;
    } catch (error) {
      console.error(error);
    }
  }

}
