import React from "react";
import axios from "axios";
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";
import HeaderComponent from "../src/components/header.component";
import InputSearchComponent from "../src/components/input.search.component";
import ResultTableComponent from "../src/components/result.table.component";

export default class App extends React.Component {


  constructor(props) {
    super(props);
    this.handleInputSearch = this.handleInputSearch.bind(this);
    this.state = { keyword: "", tweets: "" };
  }

  handleInputSearch = (e) => this.setState({ keyword: e });
  doSearchClick = (e) => this.getTweets(this.state.keyword)

  render() {
    const { tweets } = this.state;
    // this.getTweets({ keyword });
    return (
      <Router>
        <div>
          <Link to="/" label="Home">Home</Link>
          <Link to="/statistics" label="About">Statistics</Link>
          <hr />
          <div className="App container">
            <HeaderComponent
              title="Mockup for TwitterAPI"
              description="ReactJS + ExpressJS + NodeJS + TwitterAPI"
            />
            <InputSearchComponent onInputChange={this.handleInputSearch} doSearchClick={this.doSearchClick} />
          </div>
          <div className="resultArea">
            <div className="container">
            {this.state.list.map(item => (
            <li key={item}>{item}</li>
          ))}
              <ResultTableComponent tweets={tweets} />
            </div>
          </div>

          <Switch>
            <Route exact path="/">
              {/* <Home /> */}
            </Route>
            <Route path="/statistics">
              {/* <About /> */}
            </Route>
          </Switch>
        </div>
      </Router>
    )
  }

  async getTweets(keyword) {
    if(keyword.length > 0)
    try {
      const result = await axios.get("http://localhost:4000/" + keyword);
      this.state.list.push(JSON.parse(JSON.stringify(result.data)))
      // this.setState({ tweets: JSON.parse(JSON.stringify(result.data)) })
    } catch (error) {
      console.error(error);
    }
  }

}
