import React from "react";
import PaginationComponent from "./pagination.component";
import FormatDate from "../utils/formatDate";

class ResultTableComponent extends React.Component {
  constructor(props) {
    super(props);
    // this.doSearch = this.doSearch.bind(this);
    this.state = { word: "", tweets: [] };
  }

  loadData(tweets) {
    this.setState({ tweets: tweets });
      console.log(this.state.tweets);
      this.forceUpdate()
      // this.setState({word:"abc"})
  }

  render() {
    return (
      <div className="ResultTable">
        <PaginationComponent />

        {this.state.tweets.length} result(s)

        <h1>{this.state.word}</h1>
        <table
          id="dtBasicExample"
          className="table table-striped table-bordered table-sm"
          cellSpacing="0"
          width="100%"
        >
          <thead>
            <tr>
              <th className="th-sm">profile</th>
              <th className="th-sm">user</th>
              <th className="th-sm">location</th>
              <th className="th-sm">created</th>
              <th className="th-sm">tweet</th>
            </tr>
          </thead>
          <tbody>

            {this.state.tweets.map((tweet, i) => {
              return (
                <tr key={i} >
                  <td><img alt="photo_profile" src={tweet.profile_image} /></td>
                  <td>{tweet.user}</td>
                  <td>{tweet.location ? tweet.location : " - "}</td>
                  <td>{new FormatDate(new Date(tweet.created_at)).getMonthNameShort()}</td>
                  <td>{tweet.text}</td>
                </tr>
              )
            })}

            {
        /* created_at: element.created_at,
				location: element.location,
				user: element.user.name,
				profile_image: element.user.profile_image_url,
				iso_language_code: element.iso_language_code,
				text: element.text */}

          </tbody>
          <tfoot>
            <tr>
              <th className="th-sm">profile</th>
              <th className="th-sm">user</th>
              <th className="th-sm">location</th>
              <th className="th-sm">created</th>
              <th className="th-sm">tweet</th>
            </tr>
          </tfoot>
        </table>
        <PaginationComponent />
      </div >
    );
  }
}

export default ResultTableComponent;
