import React from "react";
import axios from "axios";
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";
import HeaderComponent from "../src/components/header.component";
import InputSearchComponent from "../src/components/input.search.component";
import ResultTableComponent from "../src/components/result.table.component";

export default class App extends React.Component {


  constructor(props) {
    super(props);
    this.handleChange = this.handleChange.bind(this);
    this.state = {keyword:"",tweets:""};
  }

  handleChange(e) {
    this.setState({ keyword: e.target.value });
    console.log(e.target.value);
  }

  render() {
    setInterval(() => {

    }, 1000);
    const { keyword, tweets } = this.state;
    this.getTweets({ keyword });
    return (
      <Router>
        <div>
          <Link to="/" label="Home">Home</Link>
          <Link to="/statistics" label="About">Statistics</Link>
          <hr />
          <div className="App container">
           <h1>{keyword}</h1>
            {/* {tweets} */}
            <HeaderComponent
              title="Mockup for TwitterAPI"
              description="ReactJS + ExpressJS + NodeJS + TwitterAPI"
            />
            <InputSearchComponent value={keyword} onChange={this.handleChange} />
          </div>
          <div className="resultArea">
            <div className="container">
              <ResultTableComponent tweets={tweets} />
            </div>
          </div>

          <Switch>
            <Route exact path="/">
              {/* <Home /> */}
            </Route>
            <Route path="/statistics">
              {/* <About /> */}
            </Route>
          </Switch>
        </div>
      </Router>
    )
  }

  async getTweets(temperature) {
    try {
      const result = await axios.get("http://localhost:4000/" + temperature);
      this.setState({ tweets: JSON.stringify(result) })
    } catch (error) {
      console.error(error);
    }
  }

}
