import { ExportToCsv } from 'export-to-csv';

class Export {

    options = {
        fieldSeparator: ',',
        quoteStrings: '"',
        decimalSeparator: '.',
        showLabels: true,
        showTitle: true,
        title: 'CSV',
        useTextFile: false,
        useBom: true,
        useKeysAsHeaders: true,
        // headers: ['Column 1', 'Column 2', etc...] <-- Won't work with useKeysAsHeaders present!
    };

    static export(json) {
        new ExportToCsv(this.options).generateCsv(json);
    }

}

export default Export;
