import React from "react";
import axios from "axios";
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";
import HeaderComponent from "../src/components/header.component";
import InputSearchComponent from "../src/components/input.search.component";
import ResultTableComponent from "../src/components/result.table.component";

export default class App extends React.Component {
  state = {
    tweets: null
  }
  render() {
    const { tweets } = this.state;
    this.props.word = "zumbi";
    return (
      <Router>
        <div>
          <Link to="/" label="Home">Home</Link>
          <Link to="/statistics" label="About">Statistics</Link>
          <hr />

          <div className="App container">
            {tweets}
            <HeaderComponent
              title="Mockup for TwitterAPI"
              description="ReactJS + ExpressJS + NodeJS + TwitterAPI"
            />
            <InputSearchComponent />
          </div>
          <div className="resultArea">
            <div className="container">
              <ResultTableComponent />
            </div>
          </div>

          <Switch>
            <Route exact path="/">
              {/* <Home /> */}
            </Route>
            <Route path="/statistics">
              {/* <About /> */}
            </Route>
          </Switch>
        </div>
      </Router>
    )
  }

  async getTweets(word) {
    try {
      const result = await axios.get("http://localhost:4000/" + word);
      this.setState({ tweets: JSON.stringify(result.data) })
    } catch (error) {
      console.error(error);
    }
  }

}
