import React from "react";
import axios from "axios";
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";
import HeaderComponent from "../src/components/header.component";
import InputSearchComponent from "../src/components/input.search.component";
import ResultTableComponent from "../src/components/result.table.component";

export default class App extends React.Component {

  constructor(props) {
    super(props);
    this.handleInputSearch = this.handleInputSearch.bind(this);
    this.resultsRef = React.createRef();
    this.state = { keyword: "", tweets: [] };
  }

  handleInputSearch = (e) => {
    this.setState({ keyword: e })
  };
  doSearchClick = (e) => {
    this.setState({ tweets: [] });
    this.getTweets(this.state.keyword);
    this.resultsRef.current.teste(this.state.tweets);
  }

  render() {
    return (
      <Router>
        <div>
          <div className="container text-right">
            <Link to="/" label="Home">Home</Link>
            <Link to="/statistics" label="About">Statistics</Link>
          </div>
          <hr />
          <div className="App container">
            <HeaderComponent
              title="Mockup for TwitterAPI"
              description="ReactJS + ExpressJS + NodeJS + TwitterAPI"
            />
            <InputSearchComponent onInputChange={this.handleInputSearch} doSearchClick={this.doSearchClick} />
          </div>
          <div className="resultArea">

            <Switch>
              <Route exact path="/">
                <div className="container">
                  <ResultTableComponent ref={this.resultsRef} tweets={this.state.tweets} />
                </div>
              </Route>
              <Route path="/statistics">
                {/* <About /> */}
              </Route>
            </Switch>


          </div>

        </div>
      </Router>
    )
  }

  async getTweets(keyword) {
    if (keyword.length > 0)
      try {
        (await axios.get("http://localhost:4000/" + keyword)).data.forEach(element => {
          this.state.tweets.push(element);
        });
      } catch (error) {
        console.error(error);
      }
  }

}
