import React from "react";

class PaginationComponent extends React.Component {
  constructor(props) {
    super(props);
    this.changePageChild = this.changePageChild.bind(this);
  }

  changePageChild() {
    this.props.changePageParent(this.props.currentIndex + 1);
  }

  render() {
    return (
      <div className="PaginationComponent d-flex flex-row-reverse">
        <nav aria-label="Page navigation example">
          <ul className="pagination">
            <li className="page-item"><a className="page-link"> Previous </a></li>
            {this.createTable(this.props.pages, this.props.currentIndex)}
            <li className="page-item"><a className="page-link" onClick={this.changePageChild}> Next </a></li>
          </ul>
        </nav>
      </div>
    );
  }

  createTable = (number, currentIndex) => {
    let table = [];
    for (let i = 0; i < number; i++) {
      table.push(<li key={i} className={(currentIndex == i) ? "paginate_button page-item active" : "paginate_button page-item"}><a className="page-link" > {(i + 1)} </a></li>);
    }
    return table
  }

}

export default PaginationComponent;
