import React from "react";

class InputSearchComponent extends React.Component {
  constructor(props) {
    super(props);
    this.inputRef = React.createRef();
    this.updateKeyword = this.updateKeyword.bind(this);
    this.doSearch = this.doSearch.bind(this);
  }

  updateKeyword(e) {
    this.props.onInputChange(this.inputRef.current.value);
  }

  doSearch(e) {
    this.props.doSearchClick(this.inputRef.current.value);
  }

  render() {
    return (
      <div className="InputSearchComponent">
          <div className="md-form form-lg">
            <input
              type="text"
              id="inputSearch"
              className="form-control form-control-lg"
              ref={this.inputRef} 
              onChange={this.updateKeyword} 
            />
            <label htmlFor="inputSearch">Search</label>
          </div>
          <button type="button" onClick={this.doSearch} className="btn btn-primary btn-md mt-0">
          Search
          </button>
      </div>
    );
  }
}

export default InputSearchComponent;
